INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (1, 'Biotecnología y Medio Ambiente', 'Investigación en biotecnología aplicada (verde, amarilla, blanca, gris, roja y azul), gestión ambiental, sostenibilidad y aprovechamiento de recursos naturales.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (2, 'Ciencias Agronómicas y Pecuarias', 'Estudios en sistemas de producción agropecuaria sostenible, acuicultura, genética y mejoramiento animal, nutrición animal, fitoprotección y biodiversidad.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (3, 'Agroindustria y Desarrollo Tecnológico', 'Desarrollo e innovación de productos y procesos agroindustriales, transformación de residuos agroindustriales y aprovechamiento de subproductos.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (4, 'Ciencias Matemáticas y Física', 'Investigación en matemáticas aplicadas, modelado matemático, física teórica y aplicada.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (5, 'Ciencias Biológicas y Químicas', 'Estudios en biología, química industrial, bioquímica y ciencias naturales aplicadas.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (6, 'Gestión Empresarial y Organizaciones', 'Investigación en gestión, empresa e innovación, emprendimiento, educación y responsabilidad social empresarial, desarrollo fronterizo y capacidades humanas.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (7, 'Contabilidad y Finanzas', 'Estudios en contabilidad gerencial y financiera, NIIF, epistemología contable, tributación, auditoría, revisoría fiscal y costos.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (8, 'Negocios Internacionales y Logística', 'Investigación en competitividad, comercio internacional, ciudades globales y fronteras, logística y distribución física internacional.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (9, 'Innovación y Desarrollo Regional', 'Estudios en desarrollo regional, generación de competitividad empresarial, cultura organizacional y emprendedora, organizaciones inteligentes.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (10, 'Salud Pública y Epidemiología', 'Investigación en salud pública, epidemiología, prevención de enfermedades y promoción de la salud.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (11, 'Ciencias Clínicas y Biomédicas', 'Estudios en medicina clínica, enfermería, terapias (respiratoria y ocupacional), bacteriología y ciencias biomédicas.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (12, 'Nutrición y Dietética', 'Investigación en nutrición humana, dietética terapéutica, seguridad alimentaria y metabolismo.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (13, 'Pedagogía y Educación para la Paz', 'Investigación en educación, pedagogía y culturas de paz, educación para población vulnerable, sociedad, comunicación y desarrollo, pedagogía de la resiliencia.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (14, 'Psicología y Desarrollo Humano', 'Estudios en psicología educativa, orientación vocacional y ocupacional, desarrollo humano y bienestar psicológico.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (15, 'Comunicación Social y Medios', 'Investigación en comunicación, medios audiovisuales, periodismo, opinión pública y cultura mediática.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (16, 'Artes, Diseño y Cultura', 'Estudios en artes visuales, diseño gráfico, música, gestión cultural y producción artística.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (17, 'Ciencias Jurídicas y Sociales', 'Investigación en derecho público, gobierno, justicia, derechos humanos, trabajo social e intervención comunitaria.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (18, 'Inteligencia Artificial y Sistemas', 'Investigación en sistemas inteligentes aplicados, modelado y arquitectura de sistemas inteligentes, tópicos emergentes en IA, ciencia de datos y analítica.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (19, 'Ingeniería Civil e Infraestructura', 'Estudios en construcciones civiles, vías, transporte, hidráulica, recursos hídricos y geotecnia.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (20, 'Ingeniería Electrónica y Telecomunicaciones', 'Investigación en electrónica, telecomunicaciones, procesamiento de señales y comunicaciones.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (21, 'Ingeniería Mecánica y Energía', 'Estudios en diseño mecánico, energía, materiales, manufactura y automatización industrial.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (22, 'Ingeniería Industrial y Calidad', 'Investigación en procesos industriales, gestión de calidad, productividad, logística industrial y optimización.', true);
INSERT INTO public.areas_investigacion (id, nombre, descripcion, activo) VALUES (23, 'Minería y Petróleos', 'Estudios en explotación minera, ingeniería de petróleos, geología aplicada y recursos energéticos.', true);
