INSERT INTO public.modalidades_proyecto (id, tipo_proyecto_id, nombre, descripcion) VALUES (1, 1, 'MONOGRAFIA', 'Trato sistemático, especial y completo de determinada parte de una ciencia o asunto en particular. Puede ser descriptiva o explicativa.');
INSERT INTO public.modalidades_proyecto (id, tipo_proyecto_id, nombre, descripcion) VALUES (2, 1, 'TRABAJO_INVESTIGACION', 'Actividad intelectual encaminada a la construcción de conocimientos utilizando instrumentos racionales y materiales dentro del rigor científico.');
INSERT INTO public.modalidades_proyecto (id, tipo_proyecto_id, nombre, descripcion) VALUES (3, 1, 'SISTEMATIZACION_CONOCIMIENTO', 'Organización y/o reorganización de saberes de una ciencia o disciplina, presentados en forma novedosa y didáctica.');
INSERT INTO public.modalidades_proyecto (id, tipo_proyecto_id, nombre, descripcion) VALUES (4, 2, 'TRABAJO_SOCIAL', 'Desarrollo de programas diseñados para instituciones o comunidades produciendo optimización en aspectos como educación, salud, recreación, medio ambiente, producción, etc.');
INSERT INTO public.modalidades_proyecto (id, tipo_proyecto_id, nombre, descripcion) VALUES (5, 2, 'CONSULTORIA', 'Ejercicio profesional de concepción, elaboración y presentación de proyectos de inversión, infraestructura, ingeniería o desarrollo comunitario.');
INSERT INTO public.modalidades_proyecto (id, tipo_proyecto_id, nombre, descripcion) VALUES (6, 2, 'PASANTIA', 'Rotación o permanencia del estudiante en una institución, realizando actividades propias de la profesión bajo dirección de un profesional experto.');
INSERT INTO public.modalidades_proyecto (id, tipo_proyecto_id, nombre, descripcion) VALUES (7, 2, 'TRABAJO_DIRIGIDO', 'Desarrollo de un proyecto específico bajo dirección de un profesional, siguiendo plan establecido en anteproyecto aprobado.');
